import React from 'react'
import { useState, useEffect } from 'react';

function Tag() {
    const [gif,setGif] = useState("");
    //    const url= `https://api.giphy.com/v1/gifs/random?api_key=${API_KEY}`;

       const [loading, setLoading] = useState(false);

        async function fetchData(){

            setLoading(true);

            const url= `https://api.giphy.com/v1/gifs/random?api_key=R0ZIgcMu0uHzcEluUUGDUGNJ31vxmbVP`;
            const {data}=await axios.get(url);
            const imageSource = data.data.images.downsized_large.url;
            setGif(imageSource);
            console.log(imageSource);

            setLoading(false);
        }

        useEffect( () =>{
            fetchData();
        },[])

        function clickHandler(){
           fetchData();
        }

       return (
        <div className='w-1/2  bg-green-600 rounded-lg  border
           flex flex-col  items-center gap-y-[5px]'>

            <h1 className='text-3xl font-bold underline uppercase mt-5 '> A Random GIF </h1>

            {
                loading ? (<Spinner/>) : (<img src={gif} width="450" />)
            }

            <button  className="bg-white rounded-lg w-10/12 py-2 text-2xl mt-4 mb-3" onClick={clickHandler}>
                Generate
            </button>

            <button  className="bg-white rounded-lg w-10/12 py-2 text-2xl mt-4 mb-3" onClick={clickHandler}>
                Generate
            </button>

        </div>
        )
}

export default Tag;
