import React, { useEffect, useState } from 'react'
import axios   from 'axios';


const API_KEY = process.env.REACT_APP_GIPHY_API_KEY;

const Random = () =>{

   const [gif,setGif] = useState("");
//    const url= `https://api.giphy.com/v1/gifs/random?api_key=${API_KEY}`;

   function clickHandler(){

        async function fetchData(){
            const url= `https://api.giphy.com/v1/gifs/random?api_key=R0ZIgcMu0uHzcEluUUGDUGNJ31vxmbVP`;
            const {data}=await axios.get(url);
            const imageSource = data.data.images.downsized_large.url;
            setGif(imageSource);
            console.log(imageSource);

        }

        useEffect( () =>{
            fetchData();
        },[])
  }

   return (
    <div className='w-1/2 h-[450px] bg-green-600 rounded-lg  border
       flex flex-col  items-center gap-y-[5px]'>

        <h1 className='text-3xl font-bold underline uppercase mt-10'> A Random GIF </h1>

        <img src={gif} width="450" />


        <button  className="bg-white rounded-lg w-10/12 py-2 text-2xl" onClick={clickHandler}>
            Generate
        </button>

    </div>
    )
}


export default Random;
